import React from 'react'

export default function FeaturedInfo(props) {
    const { item } = props;
    let formatter = new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: "USD",
    });
    return (
        <div>
            <img src={item?.image} alt="" />
            <h1>{item?.title}</h1>
            <h2>{item?.Goal}</h2>
            <h3>Description: {item?.Description}</h3>
            <h3>Organizer: {item?.Organizer}</h3>
        </div>
    )
}
