import React from "react";
import { useState } from "react";
import { NewCampaignForm, CampaignInputs, CampaignText, NewCamDiv, AddCamButton, formHeader } from "../styled-components/AddCampaignStyled";

export default function AddCampaign() {
  const [campaignData, setCampaignData] = useState({
    creator_name: "",
    title: "",
    image: "",
    goal: "",
    description: "",
  });

  const createCampaign = async (e) => {
    e.preventDefault();

    const createCampaign = await fetch(
      "http://localhost:3000/create_campaign",
      {
        method: "POST",
        mode: "cors",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(campaignData),
      }
    );
    window.location.reload(false);
  };
  return (
    <NewCamDiv>
      <NewCampaignForm  action="">
        <h2>Start A New Campaign Here!</h2>
        <CampaignInputs
          onChange={(e) =>
            setCampaignData({
              ...campaignData,
              [e.target.name]: e.target.value,
            })
          }
          name="creator_name"
          type="text"
          placeholder="Creator Name"
        />
        <CampaignInputs
          onChange={(e) =>
            setCampaignData({
              ...campaignData,
              [e.target.name]: e.target.value,
            })
          }
          name="image"
          type="text"
          placeholder="Image URL"
        />
        <CampaignInputs
          onChange={(e) =>
            setCampaignData({
              ...campaignData,
              [e.target.name]: e.target.value,
            })
          }
          name="title"
          type="text"
          placeholder="Title"
        />

        <CampaignInputs
          onChange={(e) =>
            setCampaignData({
              ...campaignData,
              [e.target.name]: e.target.value,
            })
          }
          name="goal"
          type="number"
          placeholder="Goal"
        />

        <CampaignText
          onChange={(e) =>
            setCampaignData({
              ...campaignData,
              [e.target.name]: e.target.value,
            })
          }
          name="description"
          id=""
          cols="30"
          rows="10"
          placeholder="Description"
        ></CampaignText>
        <AddCamButton onClick={(e) => createCampaign(e)}>Add Campaign</AddCamButton>
      </NewCampaignForm >
    </NewCamDiv>
  );
}
