            // console.log("use effect was fired")
            // console.log(campaignInfo)

        };
    setCampaignInfo(readCampaigns)
    console.log(campaignInfo)
    return (
        <div>
          {campaignInfo?.map((campaign)=>(
              <Campaigns campaign={campaign} />
          ))}
        </div>
    )
}
